import React, { useState } from 'react';
import './App.css';
import Sidebar from './Sidebar';
import HeaderBar from './HeaderBar';
import PlayerBar from './PlayerBar';
import Content from './Content';

function extractPlaylistId(input) {
  // 支持直接输入ID或网易云歌单链接
  const match = input.match(/id=(\d+)/) || input.match(/playlist\/(\d+)/) || input.match(/(\d{6,})/);
  return match ? match[1] : null;
}

function App() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isStopped, setIsStopped] = useState(true);
  const [refreshKey, setRefreshKey] = useState(0);

  // 处理HeaderBar输入
  const handleInput = async (input) => {
    // 判断是否为歌单
    const playlistId = extractPlaylistId(input);
    if (playlistId && (input.includes('playlist') || input.includes('id=') || /\d{6,}/.test(input))) {
      // 歌单批量推流
      // 1. 获取歌单所有歌曲
      const res = await fetch(`https://music-api.focalors.ltd/playlist/track/all?id=${playlistId}`);
      const data = await res.json();
      const ids = (data.songs || []).map(song => song.id);
      if (!ids.length) {
        alert('未获取到歌单歌曲');
        return;
      }
      // 2. 批量获取直链
      const urlRes = await fetch(`https://music-api.focalors.ltd/song/url?id=${ids.join(',')}`);
      const urlData = await urlRes.json();
      const urls = urlData.data.map(item => item.url).filter(Boolean);
      if (!urls.length) {
        alert('未获取到任何可用直链');
        return;
      }
      // 3. 发送到后端
      await fetch('/api/add_batch', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ musics: urls })
      });
      alert('歌单已全部加入播放队列！');
      setRefreshKey(k => k + 1);
      return;
    }
    // 否则视为歌名，搜索并点播第一首
    const res = await fetch(`https://music-api.focalors.ltd/cloudsearch?keywords=${encodeURIComponent(input)}`);
    const data = await res.json();
    const song = (data.result?.songs || [])[0];
    if (!song) {
      alert('未搜索到歌曲');
      return;
    }
    // 获取直链
    const urlRes = await fetch(`https://music-api.focalors.ltd/song/url?id=${song.id}`);
    const urlData = await urlRes.json();
    const url = urlData.data[0]?.url;
    if (url) {
      await fetch('/api/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ music: url })
      });
      alert(`已发送到机器人播放：${song.name}`);
      setRefreshKey(k => k + 1);
    } else {
      alert('获取直链失败');
    }
  };

  return (
    <div className="cloudmusic-root">
      <Sidebar />
      <div className="cloudmusic-main">
        <HeaderBar onSubmit={handleInput} />
        <div className="cloudmusic-content">
          <Content refreshKey={refreshKey} />
        </div>
        <PlayerBar isPlaying={isPlaying} setIsPlaying={setIsPlaying} isStopped={isStopped} setIsStopped={setIsStopped} />
      </div>
    </div>
  );
}

export default App;
