import React, { useEffect, useState } from 'react';

function Content({ refreshKey }) {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchList = async () => {
    setLoading(true);
    try {
      const res = await fetch('/api/list');
      const data = await res.json();
      setList(data || []);
    } catch (e) {
      setList([]);
      alert('前端获取播放列表出错，请查看控制台');
      console.error('[前端异常] fetchList:', e);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchList();
    // eslint-disable-next-line
  }, [refreshKey]);

  return (
    <div style={{ maxWidth: 600, margin: '0 auto' }}>
      <h2>当前播放列表</h2>
      {loading && <div>加载中...</div>}
      {(!loading && list.length === 0) && <div>暂无播放队列</div>}
      {list.length > 0 && (
        <table style={{ width: '100%', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #eee', marginBottom: 24 }}>
          <thead>
            <tr style={{ background: '#f7f7f7' }}>
              <th style={{ padding: 8 }}>歌名/链接</th>
              <th>备注</th>
            </tr>
          </thead>
          <tbody>
            {list.map((item, idx) => (
              <tr key={idx}>
                <td style={{ padding: 8 }}>{item.file || '-'}</td>
                <td>{item.extra?.['音乐名字'] || ''}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Content;
