import React, { useState } from 'react';

function extractPlaylistId(input) {
  // 支持直接输入ID或网易云歌单链接
  const match = input.match(/id=(\d+)/) || input.match(/playlist\/(\d+)/) || input.match(/(\d{6,})/);
  return match ? match[1] : input;
}

function Content() {
  const [search, setSearch] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);

  const [playlistInput, setPlaylistInput] = useState('');
  const [playlistLoading, setPlaylistLoading] = useState(false);

  // 歌曲搜索
  const handleSearch = async () => {
    setLoading(true);
    setSongs([]);
    const res = await fetch(`https://music-api.focalors.ltd/cloudsearch?keywords=${encodeURIComponent(search)}`);
    const data = await res.json();
    setSongs(data.result?.songs || []);
    setLoading(false);
  };

  // 歌曲播放
  const handlePlay = async (songId) => {
    const res = await fetch(`https://music-api.focalors.ltd/song/url?id=${songId}`);
    const data = await res.json();
    const url = data.data[0]?.url;
    if (url) {
      await fetch('/api/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ music: url })
      });
      alert('已发送到机器人播放！');
    } else {
      alert('获取直链失败');
    }
  };

  // 歌单批量推流
  const handlePlaylist = async () => {
    setPlaylistLoading(true);
    const playlistId = extractPlaylistId(playlistInput.trim());
    if (!playlistId) {
      alert('请输入正确的歌单链接或ID');
      setPlaylistLoading(false);
      return;
    }
    // 1. 获取歌单所有歌曲
    const res = await fetch(`https://music-api.focalors.ltd/playlist/track/all?id=${playlistId}`);
    const data = await res.json();
    const ids = (data.songs || []).map(song => song.id);
    if (!ids.length) {
      alert('未获取到歌单歌曲');
      setPlaylistLoading(false);
      return;
    }
    // 2. 批量获取直链
    const urlRes = await fetch(`https://music-api.focalors.ltd/song/url?id=${ids.join(',')}`);
    const urlData = await urlRes.json();
    const urls = urlData.data.map(item => item.url).filter(Boolean);
    if (!urls.length) {
      alert('未获取到任何可用直链');
      setPlaylistLoading(false);
      return;
    }
    // 3. 发送到后端
    await fetch('/api/add_batch', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ musics: urls })
    });
    alert('歌单已全部加入播放队列！');
    setPlaylistLoading(false);
  };

  return (
    <div style={{ maxWidth: 600, margin: '0 auto' }}>
      <h2>网易云音乐点歌</h2>
      <div style={{ display: 'flex', marginBottom: 16 }}>
        <input
          type="text"
          value={search}
          onChange={e => setSearch(e.target.value)}
          placeholder="输入歌名"
          style={{ flex: 1, padding: 8, fontSize: 16 }}
          onKeyDown={e => e.key === 'Enter' && handleSearch()}
        />
        <button onClick={handleSearch} style={{ marginLeft: 8, padding: '8px 16px', fontSize: 16 }}>
          搜索
        </button>
      </div>
      {loading && <div>搜索中...</div>}
      {songs.length > 0 && (
        <table style={{ width: '100%', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #eee', marginBottom: 24 }}>
          <thead>
            <tr style={{ background: '#f7f7f7' }}>
              <th style={{ padding: 8 }}>歌名</th>
              <th>歌手</th>
              <th>专辑</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {songs.map(song => (
              <tr key={song.id}>
                <td style={{ padding: 8 }}>{song.name}</td>
                <td>{song.ar.map(a => a.name).join(', ')}</td>
                <td>{song.al.name}</td>
                <td>
                  <button onClick={() => handlePlay(song.id)}>播放</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>歌单批量播放</h2>
      <div style={{ display: 'flex', marginBottom: 16 }}>
        <input
          type="text"
          value={playlistInput}
          onChange={e => setPlaylistInput(e.target.value)}
          placeholder="输入歌单链接或ID"
          style={{ flex: 1, padding: 8, fontSize: 16 }}
          onKeyDown={e => e.key === 'Enter' && handlePlaylist()}
        />
        <button onClick={handlePlaylist} style={{ marginLeft: 8, padding: '8px 16px', fontSize: 16 }}>
          {playlistLoading ? '处理中...' : '批量播放'}
        </button>
      </div>
    </div>
  );
}

export default Content;
