import React from 'react';
import './PlayerBar.css';

function PlayerBar({ isPlaying, setIsPlaying, isStopped, setIsStopped }) {
  const handlePlay = () => {
    setIsPlaying(true);
    setIsStopped(false);
  };
  const handlePause = () => {
    setIsPlaying(false);
  };
  const handleStop = () => {
    setIsPlaying(false);
    setIsStopped(true);
  };
  return (
    <div className="playerbar">
      <div className="playerbar-left">
        <img className="playerbar-cover" src="https://p2.music.126.net/2Qn6Qn6Qn6Qn6Qn6Qn6Qn6Q==/109951165888888888.jpg" alt="cover" />
        <div className="playerbar-songinfo">
          <div className="playerbar-title">‰∏§Èöæ</div>
          <div className="playerbar-artist">Âä†Êú®</div>
        </div>
      </div>
      <div className="playerbar-center">
        <button className="playerbar-btn" disabled><span role="img" aria-label="prev">‚èÆÔ∏è</span></button>
        {isPlaying ? (
          <button className="playerbar-btn main" onClick={handlePause}><span role="img" aria-label="pause">‚è∏Ô∏è</span></button>
        ) : (
          <button className="playerbar-btn main" onClick={handlePlay}><span role="img" aria-label="play">‚ñ∂Ô∏è</span></button>
        )}
        <button className="playerbar-btn" disabled><span role="img" aria-label="next">‚è≠Ô∏è</span></button>
        <button className="playerbar-btn" onClick={handleStop}><span role="img" aria-label="stop">‚èπÔ∏è</span></button>
      </div>
      <div className="playerbar-right">
        <span className="playerbar-time">00:01</span>
        <div className="playerbar-progress-bg">
          <div className="playerbar-progress" style={{ width: isPlaying ? '60%' : isStopped ? '0%' : '30%' }}></div>
        </div>
        <span className="playerbar-time">02:50</span>
        <button className="playerbar-btn small" disabled>‚ù§Ô∏è</button>
        <button className="playerbar-btn small" disabled>üîâ</button>
      </div>
    </div>
  );
}

export default PlayerBar;
